expression.program:
let total_count = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_preprod',
service='server',
host='nbs-control-sas1|nbs-control-sas2|nbs-control-sas3',
request='*',
sensor='Count',
type= '-'
}));

let total_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_preprod',
service='server',
host='nbs-control-sas1|nbs-control-sas2|nbs-control-sas3',
request='*',
sensor='Errors/Retriable',
type= '-'
}));

no_data_if(
count(total_count) == 0 || 
count(total_errors) == 0
);

let e = max(total_errors);
let c = max(as_vector(max(total_count), 1));
let f = e / (100 + c);

warn_if(f >= 0.1 && f < 0.15);
alarm_if(f >= 0.15);
