expression.program:
let blacklist_requests='AlterPlacementGroupMembership|StartEndpoint|StopEndpoint|ListEndpoints|KickEndpoint';

let total_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_preprod',
service='server',
host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
request='*',
sensor='Errors',
type= 'ssd|hdd',
request!='{{blacklist_requests}}'
}));

let throttle_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_preprod',
service='server',
host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
request='*',
sensor='Errors/Throttling',
type= 'ssd|hdd',
request!='{{blacklist_requests}}'
}));

let reassign_count = integrate_fn(group_lines('sum', {project="nbs", cluster="yandexcloud_preprod", service="server", host='nbs-control-*1|nbs-control-*2|nbs-control-*3', sensor="AppCriticalEvents/ReassignTablet"}));

let session_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_preprod',
service='server',
host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
request='*',
sensor='Errors/Session',
type= 'ssd|hdd',
request!='{{blacklist_requests}}'
}));

let other_errors = total_errors - throttle_errors - session_errors;

no_data_if(count(total_errors) == 0);
no_data_if(count(throttle_errors) == 0);
no_data_if(count(session_errors) == 0);

let max_session_errors = max(session_errors);
let max_throttle_errors = max(throttle_errors);
let max_total_errors = max(total_errors);
let max_other_errors = max(other_errors);

let reassign_gauge = max(reassign_count) > 0 ? 2.0 : 1;

alarm_if(max_session_errors >= 600 * reassign_gauge);
alarm_if(max_other_errors >= 1800 * reassign_gauge);

warn_if(max_session_errors >= 10 * reassign_gauge);
warn_if(max_other_errors >= 900 * reassign_gauge);
