expression.program:
let io_requests='ReadBlocks|WriteBlocks|ZeroBlocks';
let not_so_fatal_error_list='MountVolume|AlterVolume|DescribeVolume';
let ignore_errors='AssignVolume';

let io_fatal_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_preprod',
	service='server',
	host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
	request='{{io_requests}}',
	sensor='Errors/Fatal',
	type= 'ssd|hdd'
}));

let ctrl_fatal_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_preprod',
	service='server',
	host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
	request!='{{io_requests}}|{{not_so_fatal_error_list}}|{{ignore_errors}}',
	sensor='Errors/Fatal'
}));

let not_so_fatal_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_preprod',
	service='server',
	host='nbs-control-*1|nbs-control-*2|nbs-control-*3',
	request='{{not_so_fatal_error_list}}',
	sensor='Errors/Fatal'
}));

no_data_if(count(ctrl_fatal_errors) == 0);
no_data_if(count(io_fatal_errors) == 0);

// temp thresholds (see https://st.yandex-team.ru/NBS-1808)
warn_if(max(ctrl_fatal_errors) > 2);
alarm_if(max(ctrl_fatal_errors) > 5);

warn_if(max(io_fatal_errors) == 1);
alarm_if(max(io_fatal_errors) > 1);

warn_if(max(not_so_fatal_errors) > 10);
alarm_if(max(not_so_fatal_errors) > 20);
