expression.program:
let unavailable = group_lines('max', {
	project="nbs",
	cluster="yandexcloud_preprod",
	service="disk_registry",
	host="nbs-control-vla*",
	sensor="AgentsInUnavailableState"
});

let min_unavailable = min(unavailable);
let lst_unavailable = last(unavailable);

let unavailable_diff = lst_unavailable - min_unavailable;

warn_if(unavailable_diff > 1);
alarm_if(unavailable_diff > 2);
