expression.program:
let hosts = 'myt1-*';
let blacklist_hosts = '';
let blacklist_requests='AlterPlacementGroupMembership|StartEndpoint|StopEndpoint|ListEndpoints|KickEndpoint';

let fatal_errors_threshold = 1;
let throttle_errors_threshold = 50000;
// let session_errors_threshold = 1500;
let other_errors_threshold = 1000;

let total_errors = group_lines('sum', {project='nbs', cluster='yandexcloud_prod_myt', service='client', host='{{hosts}}', request='*', sensor='Errors', type= '-', host!='{{blacklist_hosts}}', request!='{{blacklist_requests}}'});
let fatal_errors = group_lines('sum', {project='nbs', cluster='yandexcloud_prod_myt', service='client', host='{{hosts}}', request='*', sensor='Errors/Fatal', type= '-', host!='{{blacklist_hosts}}', request!='{{blacklist_requests}}'});
let throttle_errors = group_lines('sum', {project='nbs', cluster='yandexcloud_prod_myt', service='client', host='{{hosts}}', request='*', sensor='Errors/Throttling', type= '-', host!='{{blacklist_hosts}}', request!='{{blacklist_requests}}'});
// let session_errors = group_lines('sum', {project='nbs', cluster='yandexcloud_prod_myt', service='client', host='{{hosts}}', request='*', sensor='Errors/Session', type= '-', host!='{{blacklist_hosts}}'});
let other_errors = total_errors - fatal_errors - throttle_errors;

let fatal_errors_sum = sum(fatal_errors);
let throttle_errors_sum = sum(throttle_errors);
// let session_errors_sum = sum(session_errors);
let other_errors_sum = sum(other_errors);
