expression.program:
let blacklist_requests='ReadBlocks|WriteBlocks|ZeroBlocks';

let total_count = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_prod_myt',
service='server',
host='nbs-control-myt*',
request='*',
request!='DescribeDiskRegistryConfig',
sensor='Count',
type= '-',
request!='{{blacklist_requests}}'
}));

let total_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_prod_myt',
service='server',
host='nbs-control-myt*',
request='*',
request!='DescribeDiskRegistryConfig',
sensor='Errors/Retriable',
type= '-',
request!='{{blacklist_requests}}'
}));

no_data_if(
count(total_count) == 0 || 
count(total_errors) == 0
);

let e = max(total_errors);
let c = max(as_vector(max(total_count), 1));
let f = (5 + e) / (100 + c);

warn_if(f >= 0.1 && f < 0.15);
alarm_if(f >= 0.15);
