expression.program:
let all_fatal_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_prod_sas',
service='server',
host='nbs-control-*',
request='*',
sensor='Errors/Fatal',
type= '-'
}));

let not_so_fatal_errors = integrate_fn(group_lines('sum', {
project='nbs',
cluster='yandexcloud_prod_sas',
service='server',
host='nbs-control-*',
request='MountVolume|AlterVolume|AssignVolume|DescribeVolume|ReadBlocks',
sensor='Errors/Fatal',
type= '-'
}));

let fatal_errors = all_fatal_errors - not_so_fatal_errors;

no_data_if(count(fatal_errors) == 0);

warn_if(max(fatal_errors) == 1);
alarm_if(max(fatal_errors) > 1);

warn_if(max(not_so_fatal_errors) > 10);
alarm_if(max(not_so_fatal_errors) > 20);
