expression.program:
let blacklist_requests='AlterPlacementGroupMembership|StartEndpoint|StopEndpoint|ListEndpoints|KickEndpoint';

let total_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_prod_vla',
	service='server',
	host='nbs-control-*',
	request='*',
	sensor='Errors',
	type= 'ssd|hdd',
	request!='{{blacklist_requests}}'
}));

let throttle_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_prod_vla',
	service='server',
	host='nbs-control-*',
	request='*',
	sensor='Errors/Throttling',
	type= 'ssd|hdd',
	request!='{{blacklist_requests}}'
}));

let session_errors = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_prod_vla',
	service='server',
	host='nbs-control-*',
	request='*',
	sensor='Errors/Session',
	type= 'ssd|hdd',
	request!='{{blacklist_requests}}'
}));

let reassign_events = integrate_fn(group_lines('sum', {
	project='nbs',
	cluster='yandexcloud_prod_vla',
	service='server',
	host='nbs-control-*',
	sensor='AppCriticalEvents/ReassignTablet'
}));

let other_errors = total_errors - throttle_errors - session_errors;
let max_session_errors = max(session_errors);
let max_other_errors = max(other_errors);

let reassign_gauge = max(reassign_events) > 0 ? 20.0 : 1;

no_data_if(count(total_errors) == 0);
no_data_if(count(throttle_errors) == 0);
no_data_if(count(session_errors) == 0);

alarm_if(max_session_errors >= 300 * reassign_gauge);
alarm_if(max_other_errors >= 1200 * reassign_gauge);

warn_if(max_session_errors >= 10 * reassign_gauge);
warn_if(max_other_errors >= 300 * reassign_gauge);
