expression.program:
let checks = group_lines('sum', {
    project='{{ project_id }}',
    cluster='{{ folder_id }}',
    service='availability',
    host='ydb-*',
    sensor='ClusterAvailability',
    check_type='select-1'
});
let oks = transform(checks, 'heaviside');
let oks_count = count(oks);
let oks_sum = sum(oks);
let slo = (oks_sum * 100) / oks_count;
let slo_text = to_fixed(slo, 3) + '%';

alarm_if(slo < 99.9);
warn_if(slo < 99.99);
